import React, { useState } from 'react';

import Buttonc1 from '../../UI/Button/Button';
// import './CourseInput.css';

import styled from 'styled-components';
const FormControl = styled.div`

  margin: 0.5rem 0;


& label {
  font-weight: bold;
  display: block;
  margin-bottom: 0.5rem;
  color : ${props => (props.onInvalid ? 'red' : '#000')};
}

& input {
  display: block;
  width: 100%;
  border: 1px solid ${props => (props.onInvalid ? 'red' : '#ccc')};
  background-color : ${props => (props.onInvalid ? '#ffd7d7' : 'transparent')};
font: inherit;
line - height: 1.5rem;
padding: 0 0.25rem;
}

& input:focus {
  outline: none;
  background: #fad0ec;
  border - color: #8b005d;
}

&.invalid input {
  border - color: red;
  background: #ffd7d7;
}

&.invalid label {
  color: red;
}
`;

const CourseInput = props => {
  const [enteredValue, setEnteredValue] = useState('');

  const [isValid, setIsValid] = useState(true);

  const goalInputChangeHandler = event => {
    if (event.target.value.trim().length > 0) {

      setIsValid(true);
    }
    setEnteredValue(event.target.value)
  };

  const formSubmitHandler = event => {
    event.preventDefault();
    if (enteredValue.trim().length === 0) {
      setIsValid(false);
      return;
    }
    //setIsValid(true);
    props.onAddGoal(enteredValue);
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <FormControl invalid={!isValid}>
        <label>Course Goal</label>
        <input type="text" onChange={goalInputChangeHandler} />
      </FormControl>
      <Buttonc1 type="submit">Add Goal</Buttonc1>
    </form >
  );
};

export default CourseInput;
